        cat("Error plotting cumulated variance\nDetail:\n")
        print(ex)
        ret<<-1
    },interrupt=function(ex){
        cat("Interrupt\n")
    },finally={
        cat("\n")
    })

    if(ret>0){
        cat("Program halted\n")
        stop()
    }

    return(ret)
}

